mapper.select:
SELECT '电仪' as 'devName', MAX(CASE WHEN SC_PART='传动部电机有无异响' THEN tempResult end ) as 'a1', MAX(CASE WHEN SC_PART='传动部电机滑环' THEN tempResult end ) as 'a2', MAX(CASE WHEN SC_PART='传动部电机轴承油位' THEN tempResult end ) as 'a3', MAX(CASE WHEN SC_PART='MQY2745高低压稀油站电机振动' THEN tempResult end ) as 'a4', MAX(CASE WHEN SC_PART='MQY2745高低压稀油站电机温度' THEN RESULT end ) as 'a5'
FROM ( SELECT SC_PART, (CASE WHEN RESULT='停机不检' THEN RESULT WHEN ((SC_PART='传动部电机有无异响' OR SC_PART='传动部电机滑环' OR SC_PART='传动部电机轴承油位' OR SC_PART='MQY2745高低压稀油站电机振动' ) AND (ALTPID = 2 OR ALTPID=1)) THEN '故障' WHEN ((SC_PART='传动部电机有无异响' OR SC_PART='传动部电机滑环' OR SC_PART='传动部电机轴承油位' OR SC_PART='MQY2745高低压稀油站电机振动' ) AND (ALTPID = 0)) THEN '正常' WHEN ALTPID is null then '未检' END ) as tempResult, RESULT FROM dxj_spot_result WHERE (( ZONE_NAME = '再磨机' OR ZONE_NAME = '球磨机稀油润滑系统' ) OR ( devName = 'MQY2745再磨机电机'
) OR devName='MQY2745再磨机高低压稀油站电机' ) AND to_days(TASKSTART_TM) = to_days(now())-- 当天数据 )as temp
