mapper.select:
SELECT
uploadResultTime AS time, devName AS equip, SC_PART AS part, SC_CONTENT AS spot, ID as id, RESULT AS value, ( CASE WHEN ALTPID = 1 THEN "二级" WHEN ALTPID = 2 THEN "一级" END ) level,
(CASE WHEN alarm_flag="N" THEN '未处理' WHEN alarm_flag="Y" THEN '已处理' END) flag, std_value AS standard FROM dxj_spot_result WHERE (ROUTE_NAME='浮选系统' OR ROUTE_NAME='浮选路线') AND ( devName = "${equip}" OR devName = CONCAT("${equip}","电机") ) AND (ALTPID = 1 OR ALTPID = 2) AND date(TASKSTART_TM) >= DATE_SUB(CURDATE(), INTERVAL 30 DAY) ORDER BY uploadResultTime DESC;
