mapper.select:
SELECT '设备' as 'devName', MAX(CASE WHEN SC_CONTENT='润滑油油质是否符合要求' THEN temResult end ) as 'a1', MAX(CASE WHEN SC_CONTENT='联轴器有无损伤' THEN temResult end ) as 'a2', MAX(CASE WHEN SC_CONTENT='润滑油油质是否符合要求' THEN temResult end ) as 'a3', MAX(CASE WHEN SC_PART='清扫器' THEN temResult end ) as 'a4', MAX(CASE WHEN SC_PART='上、下托辊' THEN temResult end ) as 'a5', MAX(CASE WHEN SC_PART='紧急拉绳装置' THEN temResult end ) as 'a6', MAX(CASE WHEN SC_PART='皮带' THEN temResult end ) as 'a7', MAX(CASE WHEN SC_PART='尾轮' THEN temResult end ) as 'a8', MAX(CASE WHEN SC_PART='尾部拉紧装置' THEN temResult end ) as 'a9' FROM ( SELECT devName,SC_CONTENT,SC_PART, (CASE WHEN RESULT='停机不检' THEN RESULT WHEN (ALTPID = 2 OR ALTPID=1) THEN '故障' WHEN ALTPID=0 THEN '正常' END
)temResult from dxj_spot_result
WHERE date(TASKSTART_TM)>= DATE_SUB(CURDATE(), INTERVAL 5 DAY) AND DEPT_NAME='磨浮设备' AND devName='5#皮带运输机' )as tmp

SELECT '电仪' as 'devName', MAX(CASE WHEN SC_CONTENT='测温' THEN Result end ) as 'a1', MAX(CASE WHEN SC_CONTENT='测振' THEN Result end ) as 'a2', MAX(CASE WHEN SC_CONTENT='有无异响' THEN tempResult end ) as 'a3', MAX(CASE WHEN SC_CONTENT='有无松动' THEN tempResult end ) as 'a4' FROM ( SELECT devName,SC_CONTENT,SC_PART,Result, (CASE WHEN RESULT='停机不检' THEN RESULT WHEN (ALTPID = 2 OR ALTPID=1) THEN '故障' WHEN ALTPID=0 THEN '正常' ELSE RESULT END
)tempResult from dxj_spot_result
WHERE TO_DAYS(TASKSTART_TM)=TO_DAYS(NOW())AND DEPT_NAME='电仪工段' AND devName='5#皮带运输机电机' )as tmp
