mapper.select:
SELECT '生产' as 'devName', MAX(CASE WHEN SC_PART='三相异步电机' AND SC_CONTENT='是否有震动' THEN temResult end ) as 'a1', MAX(CASE WHEN SC_PART='三相异步电机' AND SC_CONTENT='螺栓是否紧固' THEN temResult end ) as 'a2', MAX(CASE WHEN SC_PART='三相异步电机' AND SC_CONTENT='是否润滑正常' THEN temResult end ) as 'a3', MAX(CASE WHEN SC_PART='减速机、液压控制泵站' AND SC_CONTENT='是否有震动' THEN temResult end ) as 'a4', MAX(CASE WHEN SC_PART='减速机、液压控制泵站' AND SC_CONTENT='螺栓是否紧固' THEN temResult end ) as 'a5', MAX(CASE WHEN SC_PART='减速机、液压控制泵站' AND SC_CONTENT='油箱油位是否在要求位置' THEN temResult end ) as 'a6', MAX(CASE WHEN SC_PART='耙架提升机' AND SC_CONTENT='是否正常提升下降' THEN temResult end ) as 'a7', MAX(CASE WHEN SC_PART='耙架提升机' AND SC_CONTENT='是否有震动' THEN temResult end ) as 'a8', MAX(CASE WHEN SC_PART='耙架提升机' AND SC_CONTENT='连接是否紧固' THEN temResult end ) as 'a9', MAX(CASE WHEN SC_PART='耙架提升机' AND SC_CONTENT='是否润滑正常' THEN temResult end ) as 'a10', MAX(CASE WHEN SC_PART='中心柱轴承' AND SC_CONTENT='是否有震动' THEN temResult end ) as 'a11', MAX(CASE WHEN SC_PART='中心柱轴承' AND SC_CONTENT='螺栓是否紧固' THEN temResult end ) as 'a12', MAX(CASE WHEN SC_PART='中心柱轴承' AND SC_CONTENT='是否润滑正常' THEN temResult end ) as 'a13', MAX(CASE WHEN SC_PART='行走轮' AND SC_CONTENT='是否有震动' THEN temResult end ) as 'a14', MAX(CASE WHEN SC_PART='行走轮' AND SC_CONTENT='连接是否紧固' THEN temResult end ) as 'a15', MAX(CASE WHEN SC_PART='行走轮' AND SC_CONTENT='连接是否松动、脱节' THEN temResult end ) as 'a16', MAX(CASE WHEN SC_PART='行走轮' AND SC_CONTENT='轮胎是否异常' THEN temResult end ) as 'a17', MAX(CASE WHEN SC_PART='齿轮链条' AND SC_CONTENT='连接是否松动、脱节' THEN temResult end ) as 'a18', MAX(CASE WHEN SC_PART='齿轮链条' AND SC_CONTENT='是否润滑正常' THEN temResult end ) as 'a19', MAX(CASE WHEN SC_PART='压力表' AND SC_CONTENT='阻力是否在正常范围内' THEN temResult end ) as 'a20' FROM ( SELECT devName,SC_CONTENT,SC_PART, ( CASE WHEN RESULT='停机不检' THEN RESULT WHEN (ALTPID = 2 OR ALTPID=1) THEN '故障' WHEN ALTPID=0 THEN '正常' ELSE RESULT END -- )temResult from dxj_spot_result WHERE (DEPT_NAME="精尾生产" AND devName="NJG型周边传动浓缩机（硫浓密）")
AND TO_DAYS(TASKSTART_TM)=TO_DAYS(NOW())
)as tmp
