mapper.select:
SELECT devName as equipName, (CASE WHEN SC_PART='轴承' AND SC_CONTENT='测温' THEN '轴承温度' WHEN SC_PART='轴承' AND SC_CONTENT='测振' THEN '轴承振动' WHEN SC_PART='本体' AND SC_CONTENT='有无异常' THEN '电机有无异响' WHEN SC_PART='本体' AND SC_CONTENT='有无异响' THEN '电机有无异响' WHEN SC_PART='本体' AND SC_CONTENT='有无松动' THEN '紧固螺栓有无松动' END )as part, RESULT as result,ALTPID as altpid ,uploadResultTime
from dxj_spot_result WHERE DEPT_NAME='电仪工段' AND TASKSTART_TM="${time}" AND ROUTE_NAME='碎矿系统' AND (ZONE_NAME='重板' OR ZONE_NAME='颚破')
GROUP BY equipName,SC_CONTENT ORDER BY FIELD(equipName,'简摆颚式破碎机电机','颚破稀油站1号油泵电机','颚破稀油站2号油泵电机','重型板式给料机电机','湿式除尘风机电机') ,FIELD(part,'轴承温度','轴承振动','电机有无异响','紧固螺栓有无松动')
